import { Override, Data } from "framer"

// Override Docs: https://framer.com/docs/overrides

const data = Data({ setSheetPosition: "", numSheetUpdate: 0 })

export function TapToBottom(): Override {
    return {
        onTap() {
            data.setSheetPosition = "bottom"
            data.numSheetUpdate = ++data.numSheetUpdate
        },
    }
}

export function TapToMiddle(): Override {
    return {
        onTap() {
            data.setSheetPosition = "middle"
            data.numSheetUpdate = ++data.numSheetUpdate
        },
    }
}

export function TapToTop(): Override {
    return {
        onTap() {
            data.setSheetPosition = "top"
            data.numSheetUpdate = ++data.numSheetUpdate
        },
    }
}

export function ListenTaps(): Override {
    return {
        setSheetPosition: data.setSheetPosition,
        numSheetUpdate: data.numSheetUpdate,
    }
}
